SELECT
COALESCE(SUM(use_quantity),0) AS peakQuantity,
COALESCE(SUM(fees),0) AS peakFees
FROM electric_meter_peak_reading_day
WHERE 1 = 1
AND read_time >= #{startTime}
AND read_time <= #{endTime}
AND meter_no IN

    #{item}

SELECT
read_time as dateTime,
round(use_quantity,2) as dateValue
FROM electric_meter_peak_reading_day
WHERE read_time >= #{startTime}
AND read_time <= #{endTime}
AND meter_no IN

    #{item}

ORDER BY read_time ASC

SELECT
LEFT(read_time,7) AS dateTime,
round(SUM(use_quantity),2) as dateValue
FROM electric_meter_peak_reading_day
WHERE read_time >= #{startTime}
AND read_time <= #{endTime}
AND meter_no IN

    #{item}
